create PROCEDURE [dbo].[NewStorageBillNo] @billtype int,@storagebilltypecode varchar(10),@BillNo varchar(20) output AS
  
  declare @LastSubBillNo int
  declare @NewSubBillNo int

  --入库单据
  if @billtype=0 
    set @LastSubBillNo=(select top 1 SUBSTRING (billno,10,len(billno)-9) from instoragebill where storagebilltypecode=@storagebilltypecode order by indate desc)
  --出库单据  
  else if @billtype=1
    set @LastSubBillNo=(select top 1 SUBSTRING (billno,10,len(billno)-9) from outstoragebill where storagebilltypecode=@storagebilltypecode order by outdate desc)

print @LastSubBillNo

  if @LastSubBillNo=null
    set @BillNo=@storagebilltypecode+convert(char(4),getdate(),102)+convert(char(2),getdate(),100)+convert(char(2),getdate(),103)+'01'
  else
  begin
    if @LastSubBillNo=99
      set @NewSubBillNo='00'
    else
      set @NewSubBillNo=@LastSubBillNo+1
    if len(@NewSubBillNo)=1 --补零
      set @BillNo=@storagebilltypecode+convert(char(4),getdate(),102)+convert(char(2),getdate(),100)+convert(char(2),getdate(),103)+'0'+convert(varchar(5),@NewSubBillNo)
    else
      set @BillNo=@storagebilltypecode+convert(char(4),getdate(),102)+convert(char(2),getdate(),100)+convert(char(2),getdate(),103)+convert(varchar(5),@NewSubBillNo)
  end

print @billno
