create PROCEDURE [dbo].[genBarCode] @barcode varchar(10) output AS

  declare @Last_barcode int
  declare @temp varchar(10)

  declare @barcode_length int
  declare @@genbarcodeisrunning int

  set @barcode_length=4  --barcode 长度

  --通过 syssetup 表 获得 barcode 
  DECLARE syssetup_barcode_cursor CURSOR FOR
  SELECT value FROM syssetup where parametercode='barcode'

  OPEN syssetup_barcode_cursor
  
  FETCH NEXT FROM syssetup_barcode_cursor into @Last_barcode

  WHILE @@FETCH_STATUS = 0
  BEGIN   
    FETCH NEXT FROM syssetup_barcode_cursor into @Last_barcode
  end
  
  CLOSE syssetup_barcode_cursor
  DEALLOCATE syssetup_barcode_cursor

  if @last_barcode=9999
  begin
    set @last_barcode=0
  end

  set @Last_barcode=@Last_barcode+1
  set @temp=@Last_barcode

  while len(@temp)<@barcode_length
  begin
    set @temp='0'+@temp
  end

  update syssetup set value=@Last_barcode where parametercode='barcode'
  
  set @barcode=@temp
GO
