CREATE PROCEDURE [dbo].[proc_AddFoodtoBill] 

    @SerialNo varchar(36),
    @FoodGuid varchar(36),
	@BillNo varchar(18),
    @BillTypeCode varchar(10),
	@FoodCode varchar(18),
    @FoodPrice varchar(10),
	@Operandi varchar(50),
	@Taste varchar(50),
    @AddQuantity decimal(18,2),
    @DecQuantity decimal(18,2),
	@OperatorID varchar(12),
    @TimeBlock varchar(12),
    @OperationCode char(2), --1 = 加菜  -1 = 减菜
    @suitcode varchar(20),
    @suitquantity decimal(18,2),
    @barcode varchar(10) output

AS
  --根据 @TimeBlock 返回 TimeBlockCode
  declare @TimeBlockCode varchar(8) 
  declare @Begintime datetime 

  set @TimeBlockCode = (select top 1 timeblockcode from timeblock where [name]=@TimeBlock)

  set @Begintime=getdate()

  --创建菜品营业条码
  declare @newbarcode varchar(10)
  exec genbarcode @newbarcode output

  if @suitquantity>0 
  begin
    insert into salelist_t (SerialNo,FoodGuid,BillNo,BillTypeCode,FoodCode,Add_Dec,AddQuantity,DecQuantity,Price,TimeBlockCode,BeginTime,Operandi,Taste,OperatorID,suitcode,suitquantity,barcode)
      values 
    (@SerialNo,@FoodGuid,@BillNo,@BillTypeCode,@FoodCode,@OperationCode,@AddQuantity,@DecQuantity,@suitquantity*cast(@FoodPrice as decimal(10,2)),@TimeBlockCode,@BeginTime,@Operandi,@Taste,@OperatorID,@suitcode,@suitquantity,@newbarcode)
  end
  else
  begin
    insert into salelist_t (SerialNo,FoodGuid,BillNo,BillTypeCode,FoodCode,Add_Dec,AddQuantity,DecQuantity,Price,TimeBlockCode,BeginTime,Operandi,Taste,OperatorID,suitcode,suitquantity,barcode)
      values 
    (@SerialNo,@FoodGuid,@BillNo,@BillTypeCode,@FoodCode,@OperationCode,@AddQuantity,@DecQuantity,cast(@OperationCode as int)*cast(@FoodPrice as decimal(10,2)),@TimeBlockCode,@BeginTime,@Operandi,@Taste,@OperatorID,@suitcode,@suitquantity,@newbarcode)
  end  
  set @barcode=@newbarcode
