create PROCEDURE [dbo].[proc_TableUseFreq_sum] 
  @begintime varchar(20),
  @endtime varchar(20)

AS

  delete from TableUseFreq
  insert into TableUseFreq (tableno,psncount,freq) select tableno,sum(openpsn) as psncount,count(*) as freq from view_BalanceTable where begintime>=@begintime and begintime<=@endtime group by tableno

  declare @TableNo varchar(10)
  declare @TableTypeName varchar(20)
  declare @FoodCost money

  DECLARE cursor1 CURSOR FOR
  SELECT tableno from tableusefreq

  OPEN cursor1
  
  FETCH NEXT FROM cursor1 into @TableNo

  WHILE @@FETCH_STATUS = 0
  BEGIN   
    set @TableTypeName=(select name from view_tablelist where tableno=@tableno)
    update Tableusefreq set tabletypename=@TableTypeName where tableno=@tableno

    set @FoodCost =(select sum(foodcost) as foodcost from balance where tableno=@tableno and begintime>=@begintime and begintime<=@endtime)
    update Tableusefreq set foodcost=@foodcost where tableno=@tableno
   
    FETCH NEXT FROM cursor1 into @TableNo 
  end
  CLOSE cursor1
  DEALLOCATE cursor1
GO
