create PROCEDURE [dbo].[proc_TableType_sum] 
  @begintime varchar(20),
  @endtime varchar(20)

AS

  delete from Tabletype_sum

    insert into Tabletype_sum 
       (tabletypename,psncount,foodcost,discountcost,servercost,addcost,overtimecost,oddcost,presentcost,needcost,deratecost,balancecost) 
    select 
       [name],sum(openpsn) as psncount,sum(foodcost) as foodcost,sum(discountcost) as discountcost,sum(servercost) as servercost,sum(addcost) as addcost,sum(overtimecost) overtimecost,sum(oddcost) as oddcost,sum(presentcost) as presentcost,sum(needcost) as needcost,sum(deratecost) as deratecost,sum(balancecost) as balancecost from view_balance 
    where begintime>=@begintime and begintime<=@endtime    
    group by [name]
  
  declare @TableTypeName varchar(20)
  declare @TableUseFreq int

  DECLARE cursor1 CURSOR FOR
  SELECT tabletypename from Tabletype_sum

  OPEN cursor1
  
  FETCH NEXT FROM cursor1 into @TableTypeName

  WHILE @@FETCH_STATUS = 0
  BEGIN   
    set @TableUseFreq=(select count(*) as tableusefreq from view_balance where name=@TableTypeName)
    update tabletype_sum set tableusefreq=@TableUseFreq where tabletypename=@TableTypeName

    FETCH NEXT FROM cursor1 into @TableTypeName 
  end
  CLOSE cursor1
  DEALLOCATE cursor1
GO
