create PROCEDURE [dbo].[proc_dept_sale_sum] 
  @begintime varchar(20),
  @endtime varchar(20)

AS

  delete from dept_sale_sum 

  declare @serialno varchar(36)
  declare @salecost money
  declare @discountrate money
  declare @deptname varchar(20)
  declare @paytime varchar(10)

  DECLARE cursor1 CURSOR FOR
  SELECT serialno,paytime from view_balance where paytime>=@begintime and paytime<=@endtime

  OPEN cursor1
  
  FETCH NEXT FROM cursor1 into @serialno,@paytime

  WHILE @@FETCH_STATUS = 0
  BEGIN  
    --遍历view_salelist 
    DECLARE cursor2 CURSOR FOR
    SELECT deptname,price as salecost,discountrate from view_salelist where serialno=@serialno

    OPEN cursor2
  
    FETCH NEXT FROM cursor2 into @deptname,@salecost,@discountrate

    WHILE @@FETCH_STATUS = 0
    BEGIN   

      insert into dept_sale_sum (yearmonth,deptname,salecost,discountcost) values 
      (convert(varchar(4),datepart(year,@paytime)) +convert(varchar(4),datepart(month,@paytime)),@deptname,@salecost,@salecost-@salecost*@discountrate/100)


      FETCH NEXT FROM cursor2 into  @deptname,@salecost,@discountrate
    end
    CLOSE cursor2
    DEALLOCATE cursor2

   
    FETCH NEXT FROM cursor1 into @serialno,@paytime
  end
  CLOSE cursor1
  DEALLOCATE cursor1

  update dept_sale_sum set balancecost=salecost-discountcost
GO
