create PROCEDURE [dbo].[proc_dept_cost_sum] 
  @begintime varchar(20),
  @endtime varchar(20)

AS

  delete from dept_cost_sum  

  declare @deptname varchar(20)
  declare @outdate varchar(10)
  declare @storagebilltypecode varchar(2)
  declare @depcode varchar(2)
  declare @storagetypename varchar(20)
  declare @cost money

  DECLARE cursor1 CURSOR FOR
  SELECT outdate,deptname,storagebilltypecode,depcode,totalprice 
    from view_outstoragematerials 
    where outdate>=@begintime and outdate<=@endtime

  OPEN cursor1
  
  FETCH NEXT FROM cursor1 into @outdate,@deptname,@storagebilltypecode,@depcode,@cost

  WHILE @@FETCH_STATUS = 0
  BEGIN  
   if @depcode='09'
   begin
     set @storagetypename='物料'
   end
   else
   begin
     set @storagetypename='原料'
   end

   insert into dept_cost_sum (yearmonth,deptname,storagetypename,cost) values 
          (convert(varchar(4),datepart(year,@outdate)) +convert(varchar(4),datepart(month,@outdate)),
          @deptname,
          @storagetypename,
          @cost)

    FETCH NEXT FROM cursor1 into @outdate,@deptname,@storagebilltypecode,@depcode,@cost
  end

  CLOSE cursor1
  DEALLOCATE cursor1
GO
/****** Object:  StoredProcedure [dbo].[proc_dept_stat_sum]    Script Date: 12/06/2011 16:39:39 ******/
SET ANSI_NULLS OFF
