create PROCEDURE [dbo].[proc_dept_stat_sum] 
  @begintime varchar(20),
  @endtime varchar(20)

AS

  declare @yearmonth varchar(10)
  declare @deptname varchar(20)
  declare @salecost money
  declare @discountcost money
  declare @balancecost money
  declare @consumecost money
  declare @profitrate decimal(18,2)

  delete from dept_sale_sum 
  delete from dept_cost_sum 
  delete from dept_stat

  exec proc_dept_sale_sum @begintime,@endtime
  exec proc_dept_cost_sum @begintime,@endtime

  DECLARE cursor1 CURSOR FOR
  select yearmonth,deptname,sum(salecost) as salecost,sum(discountcost) as discountcost,sum(balancecost) as balancecost 
    from dept_sale_sum group by yearmonth,deptname

  OPEN cursor1
  
  FETCH NEXT FROM cursor1 into @yearmonth,@deptname,@salecost,@discountcost,@balancecost

  WHILE @@FETCH_STATUS = 0
  BEGIN  
    insert into dept_stat (yearmonth,deptname,salecost,discountcost,balancecost) values 
      (@yearmonth,@deptname,@salecost,@discountcost,@balancecost)

    DECLARE cursor2 CURSOR FOR
    SELECT cost from dept_cost_sum where yearmonth=@yearmonth and deptname=@deptname

    OPEN cursor2
  
    FETCH NEXT FROM cursor2 into @consumecost

    WHILE @@FETCH_STATUS = 0
    BEGIN   
     --更新消耗及利润率
      set @profitrate=1-@consumecost/@balancecost

      update dept_stat set consumecost=@consumecost,profitrate=@profitrate*100 where yearmonth=@yearmonth and deptname=@deptname

      FETCH NEXT FROM cursor2 into  @consumecost
    END
    CLOSE cursor2
    DEALLOCATE cursor2

    FETCH NEXT FROM cursor1 into @yearmonth,@deptname,@salecost,@discountcost,@balancecost
  END

  CLOSE cursor1
  DEALLOCATE cursor1
GO
